/* DOMPurify Placeholder - This will be replaced with actual minified version in production */
/* For now, this is a placeholder to prevent build errors */
/* In production, this should be replaced with: https://cdnjs.cloudflare.com/ajax/libs/dompurify/3.0.6/purify.min.js */

window.DOMPurify = {
  sanitize: function (dirty) {
    // Basic XSS protection placeholder
    if (typeof dirty !== "string") return dirty;
    return dirty
      .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, "")
      .replace(/<iframe\b[^<]*(?:(?!<\/iframe>)<[^<]*)*<\/iframe>/gi, "")
      .replace(/javascript:/gi, "")
      .replace(/on\w+\s*=/gi, "");
  },
};
